import { Menu, Icon } from "antd";
import React, { Component, Fragment } from "react";

const { SubMenu } = Menu;

class SideMenu extends Component {
  state = {
    theme: this.props.theme,
    folder: this.props.folder,
    collapsed: false,
    value: undefined,
    treeData: [
      { id: 1, pId: 0, value: "1", title: "Expand to load" },
      { id: 2, pId: 0, value: "2", title: "Expand to load" },
      { id: 3, pId: 0, value: "3", title: "Tree Node", isLeaf: true }
    ]
  };

  onChange = value => {
    console.log(value);
    this.setState({ value });
  };

  genTreeNode = (parentId, isLeaf = false) => {
    const random = Math.random()
      .toString(36)
      .substring(2, 6);
    return {
      id: random,
      pId: parentId,
      value: random,
      title: isLeaf ? "Tree Node" : "Expand to load",
      isLeaf
    };
  };

  onLoadData = treeNode =>
    new Promise(resolve => {
      const { id } = treeNode.props;
      setTimeout(() => {
        this.setState({
          treeData: this.state.treeData.concat([
            this.genTreeNode(id, false),
            this.genTreeNode(id, true)
          ])
        });
        resolve();
      }, 300);
    });

  toggle = () => {
    this.setState({ collapsed: !this.state.collapsed });
  };

  buildSideBar = folder => {
    if (!folder.folders && !folder.documents) {
      return undefined;
    }

    var res = [];
    if (folder.documents) {
      res.concat(
        folder.documents.map(doc => (
          <Menu.Item key={doc.id}>
            <Icon type="file-markdown" />
            {doc.title}
          </Menu.Item>
        ))
      );
    }
    // if (folder.folders){
    //   res.concat()
    // }

    return res;
    return folder.documents
      .map(doc => (
        <Menu.Item key={doc.id}>
          <Icon type="file-markdown" />
          {doc.title}
        </Menu.Item>
      ))
      .concat(
        <SubMenu
          key={folder.folders.id}
          title={
            <span>
              <Icon type="folder" title={folder.folders.folderName} />
            </span>
          }
        >
          {folder.folders.map(new_folder => this.buildSideBar(new_folder))}
          {/* {this.buildSideBar(folder.folders)} */}
        </SubMenu>
      );
  };

  render() {
    return (
      <Menu
        theme={this.state.theme}
        style={{ width: "100%", height: this.props.height }}
        mode="inline"
        inlineCollapsed={this.state.collapsed}
        style={{ height: this.props.height, width: this.props.width }}
      >
        {this.buildSideBar(this.state.folder)}
      </Menu>
    );
