import React, { Component } from "react";
import { Layout, Row, Col } from "antd";
import SideMenu from "./sideMenu";
import TopMenu from "./topMenu";
import EditArea from "./editArea";
import ContentShow from "./contentShow";
import "@ant-design/dark-theme";
import "~antd/dist/antd.css";

const { Footer, Content } = Layout;

class EditPage extends Component {
  state = {
    theme: "dark",
    value:
      "# 一级标题\n## 二级标题\n### 三级标题\n#### 四级标题\n##### 五级标题",
    height: "89vh"
  };

  onChange = value => {
    console.log("new value", value);
    this.setState({ value });
  };

  render() {
    return (
      <div>
        <Layout style={{ height: "100vh" }}>
          <TopMenu theme={this.state.theme} />
          <Layout>
            <Content style={{ height: "88vh" }}>
              <Row>
                <Col span={2}>
                  <SideMenu height={this.state.height} />
                </Col>
                <Col span={11}>
                  <EditArea
                    onChange={this.onChange}
                    value={this.state.value}
                    height={this.state.height}
                  />
                </Col>
                <Col span={11}>
                  <ContentShow value={this.state.value} />
                </Col>
              </Row>
            </Content>
          </Layout>
          <Footer>@CopyRight SUSTech Doc by Xinghe Yao</Footer>
        </Layout>
      </div>
    );
  }
}

export default EditPage;
