import React, { Component } from "react";
import { Card, Col, Row, Avatar, Icon } from "antd";

const { Meta } = Card;

class DocCard extends Component {
  state = { documents: this.props.documents };

  groupBy = (list, name) => {
    return list.reduce((obj, item) => {
      if (!obj[item[name]]) {
        obj[item[name]] = [];
        obj[item[name]].push(item);
      } else {
        obj[item[name]].push(item);
      }
      return obj;
    }, {});
  };
  render() {
    const selected = this.props.selected;
    const { documents } = this.state;
    const sorted = this.groupBy(documents, "title");
    if (selected === "1") {
      {
        const sorted = this.groupBy(this.documents, doc => doc.title);
        console.log(sorted);
      }
      return (
        <div style={{ background: "#ECECEC", padding: "30px" }}>
          <Row>
            {documents.map(doc => (
              <Col span={12} key={doc.id}>
                <Card
                  style={{ width: "90%", margin: "20px" }}
                  actions={[
                    <Icon type="setting" key="setting" />,
                    <Icon type="edit" key="edit" />,
                    <Icon type="ellipsis" key="ellipsis" />
                  ]}
                >
                  <Meta
                    avatar={
                      <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                    }
                    title="Card title"
                    description="This is the description"
                  />
                </Card>
              </Col>
            ))}
          </Row>
        </div>
      );
    }
    return <h2>Todo</h2>;
  }
}

export default DocCard;
