import React, { Component } from "react";
import { Card, Col, Row, Avatar, Icon, Typography } from "antd";

const { Meta } = Card;
const { Text } = Typography;

class DocCard extends Component {
  state = { documents: this.props.documents };

  sort_by_date = documents => {
    let result = [];
    for (let i = 0; i < documents.length; i++) {
      let doc = documents[i];
      let t = new Date(doc.createTime).getDay();
      if (!result[t]) {
        result[t] = [doc];
      } else {
        result[t].push(doc);
      }
    }
    return result;
  };

  formatDate = date => {
    var monthNames = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December"
    ];

    var day = date.getDate();
    var monthIndex = date.getMonth();
    var year = date.getFullYear();

    return day + " " + monthNames[monthIndex] + " " + year;
  };

  render() {
    const selected = this.props.selected;
    const { documents } = this.state;
    if (selected === "1") {
      const sorted_documents = this.sort_by_date(documents);
      console.log(sorted_documents);
      return (
        <div style={{ background: "#ECECEC", padding: "30px" }}>
          <Row>
            {sorted_documents.map(e => (
              <div>
                <div>
                  <Text disabled>
                    {this.formatDate(new Date(e[0].createTime))}
                  </Text>
                </div>
                {e.map(doc => (
                  <Col span={8} key={doc.id}>
                    <Card
                      style={{ width: "97%", margin: "5px" }}
                      actions={[
                        <Icon type="setting" key="setting" />,
                        <Icon type="edit" key="edit" />,
                        <Icon type="ellipsis" key="ellipsis" />
                      ]}
                    >
                      <Meta
                        avatar={
                          <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                        }
                        title="Card title"
                        description="This is the description"
                      />
                    </Card>
                  </Col>
                ))}
              </div>
            ))}
          </Row>
        </div>
      );
    }
    return <h2>Todo</h2>;
  }
}

export default DocCard;
