    let result = [];
    for (let i = 0; i < documents.length; i++) {
      let doc = documents[i];
      let t = new Date(doc.createTime).getDay();
      if (!result[t]) {
        result[t] = [doc];
      } else {
        result[t].push(doc);
      }
    }
    return result;
