import React, { Component } from "react";
import AppNavbar from "./appNavbar";
import AppSidebar from "./appSidebar";
import DocCard from "./docCard";
import { Row, Col } from "antd";
import UpLoad from "./upLoadButton";
import NewBotton from "./newBotton";

class App extends Component {
  state = {
    id: 1,
    selected: "1",
    isLoading: true,
    documents: [],
    appNavbarWidth: "100%",
    appNavbarHeight: "10%",
    appSidebarWidth: "100%",
    appSidebarHeight: "90vh",
    appButtonWidth: "150px"
  };

  onChange = e => {
    this.setState({ selected: e.key });
  };

  async componentDidMount() {
    const response = await fetch(`/api/documents`);
    const body = await response.json();
    this.setState({ isLoading: false, documents: body });
  }

  render() {
    const {
      isLoading,
      selected,
      documents,
      appNavbarHeight,
      appNavbarWidth,
      appSidebarHeight,
      appSidebarWidth,
      appButtonWidth
    } = this.state;
    if (isLoading) {
      return <h2>Loading...</h2>;
    }
    return (
      <div>
        <div style={{ position: "fixed", zIndex: 1, width: "100%" }}>
          <AppNavbar width={appNavbarWidth} height={appNavbarHeight} />
        </div>
        <div>
          <Row>
            <Col span={3}>
              <div
                style={{
                  position: "fixed",
                  zIndex: 1,
                  marginTop: 50,
                  overflow: "auto",
                  width: "12.5%"
                }}
              >
                <AppSidebar
                  width={appSidebarWidth}
                  height={appSidebarHeight}
                  documents={documents}
                  selected={selected}
                  onChange={this.onChange}
                />
              </div>
            </Col>
            <Col span={18}>
              <div style={{ marginTop: 50 }}>
                <DocCard documents={documents} selected={selected} />
              </div>
            </Col>
            <div style={{ textAlign: "center" }}>
              <Col span={3}>
                <div style={{ position: "fixed", zIndex: 1, marginTop: 50 }}>
                  <div style={{ textAlign: "center" }}>
                    <UpLoad width={appButtonWidth} />
                    <NewBotton width={appButtonWidth} />
                  </div>
                </div>
              </Col>
            </div>
          </Row>
        </div>
      </div>
    );
  }
}

export default App;
