import React, { Component } from "react";
import AppNavbar from "./appNavbar";
import { Tabs, Radio, Col, Row, Icon, Input, Button } from "antd";

const { TabPane } = Tabs;
const { Search } = Input;

class SelfInfomation extends Component {
  state = {
    firstColSpan: 5,
    secondColSpan: 15,
    thirdColSpan: 4,
    appNavbarWidth: "100%",
    appNavbarHeight: "10%",
    mode: "left",
    avatar_url:
      "https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
  };
  render() {
    const {
      appNavbarWidth,
      appNavbarHeight,
      firstColSpan,
      secondColSpan,
      thirdColSpan
    } = this.state;
    return (
      <div>
        <div
          style={{
            position: "fixed",
            zIndex: 1,
            width: "100%",
            background: "#f7f7f7"
          }}
        >
          <AppNavbar width={appNavbarWidth} height={appNavbarHeight} />
        </div>
        <div>
          <Row>
            <Col span={6} offset={9}>
              <Tabs
                tabPosition={this.state.mode}
                defaultActiveKey="1"
                style={{ height: 400, width: 600, marginTop: 100 }}
              >
                <TabPane tab={"账号信息"} key={1}>
                  <Row style={{ marginTop: 10 }}>
                    <Col span={firstColSpan}>
                      <Icon type="user" />
                      <span>昵称</span>
                    </Col>
                    <Col span={secondColSpan}>
                      <Search />
                    </Col>
                    <Col span={thirdColSpan}></Col>
                  </Row>
                  <Row style={{ marginTop: 10 }}>
                    <Col span={firstColSpan}>
                      <Icon type="password" />
                      <span>密码</span>
                    </Col>
                    <Col span={secondColSpan}>
                      <p disabled>未设置</p>
                    </Col>
                    <Col span={thirdColSpan}>
                      <Button type="primary">设置</Button>
                    </Col>
                  </Row>
                </TabPane>
              </Tabs>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}

export default SelfInfomation;
